mapper.update:
update bbs_area
set title = #{title,jdbcType=VARCHAR},
  pid = #{pid,jdbcType=INTEGER},
  sort = #{sort,jdbcType=INTEGER}
where area_id = #{areaId,jdbcType=INTEGER}

mapper.select:
select

from bbs_area


    and area_id = #{areaId}


    and title LIKE CONCAT('%',#{title},'%')


    and pid = #{pid}
