mapper.select:
select

from message_count


    and id = #{id}


    and user_id = #{userId}


    and mobile = #{mobile}


    and send_ip = #{sendIp}

select

from message_count


    and user_id = #{userId}


    and mobile = #{mobile}


    and send_ip = #{sendIp}


    and message_type = #{messageType}


    and send_date >= #{oneHourAgoDate}


    and send_date <= #{currentSendDate}
