mapper.select:
select

from order_info


    and id = #{id}


    and order_id = #{orderId}


    and business_id = #{businessId}


    and user_id = #{userId}


    and project_type_id = #{projectTypeId}


    and project_id = #{projectId}


    and order_status = #{orderStatus}


       and DATE_FORMAT(order_date, '%Y-%m-%d')>=  DATE_FORMAT(#{orderDateBegin}, '%Y-%m-%d')   


      and DATE_FORMAT(order_date, '%Y-%m-%d') <= DATE_FORMAT(#{orderDateEnd}, '%Y-%m-%d')    


       and DATE_FORMAT(order_pay_date, '%Y-%m-%d')>=  DATE_FORMAT(#{orderPayDateBegin}, '%Y-%m-%d')   


      and DATE_FORMAT(order_pay_date, '%Y-%m-%d') <= DATE_FORMAT(#{orderPayDateEnd}, '%Y-%m-%d')
