mapper.select:
select

from project_info

  and 1 = 1

    and id = #{id}


    and user_id = #{userId}


    and project_title = #{projectTitle}


    and project_type_id = #{projectTypeId}


    and project_type_name = #{projectTypeName}


    and is_delete = #{isDelete}

select a.id, a.user_id userId, a.user_name userName, a.project_type_id projectTypeId, a.project_type_name projectTypeName, a.project_title projectTitle, a.project_content projectContent, a.project_ascription_company projectAscriptionCompany, a.province_id provinceId, a.city_id cityId, a.area_id areaId, a.work_address workAddress, a.gender_requirement genderRequirement, a.project_label projectLabel, a.salary_unit salaryUnit, a.settlement_cycle settlementCycle, a.work_welfares_id workWelfaresId, a.project_need_num projectNeedNum, a.project_contacts_name projectContactsName, a.project_contacts_mobile projectContactsMobile, a.project_contacts_email projectContactsEmail, a.project_skill projectSkill, a.work_time_num workTimeNum, a.work_time_unit workTimeUnit, a.work_time_interval_min workTimeIntervalMin, a.work_time_interval_max workTimeIntervalMax, a.work_time_requirement workTimeRequirement, a.project_begin_time projectBeginTime, a.project_end_time projectEndTime, a.project_remark projectRemark, a.order_id orderId , a.totalPay_amount totalPayAmount, a.service_charge serviceCharge, a.service_charge_rate serviceChargeRate, a.order_state orderState, a.project_state projectState, a.examine_status examineStatus, a.is_delete isDelete, a.founder, a.modifier, a.create_date createDate, a.update_date updateDate, b.work_date_begin workDateBegin, b.work_date_end workDateEnd, c.work_time_begin workTimeBegin, c.work_time_end workTimeEnd from project_info as a left join project_work_date_info as b on a.id = b.project_id left join project_work_time_info as c on a.id = c.project_id
and 1 = 1
and a.user_id = #{userId}
and a.project_type_id = #{projectTypeId}
and a.project_type_name = #{projectTypeName}
and a.project_title = #{projectTitle}
and a.project_ascription_company like CONCAT('%',#{projectAscriptionCompany},'%')
and a.province_id = #{provinceId}
and a.city_id = #{cityId}
and a.area_id = #{areaId}
and a.gender_requirement = #{genderRequirement}
and a.project_label
#{projectLabels}
and a.settlement_cycle = #{settlementCycle}
and a.project_contacts_name = #{projectContactsName}
and a.is_delete = #{isDelete}

select

from project_info where 1 = 1

  and id = #{id}


  and project_type_id = #{projectTypeId}


  and project_type_name = #{projectTypeName}


  and create_user_id = #{createUserId}


  and create_user_name = #{createUserName}


  and project_title = #{projectTitle}


  and project_status = #{projectStatus}


  and examine_status = #{examineStatus}


  and project_need_people = #{projectNeedPeople}


  and is_direct_hire = #{isDirectHire}


  and is_trusteeship = #{isTrusteeship}


  and trusteeship_amount = #{trusteeshipAmount}


  and project_create_date >= TO_DATE(#{projectCreateDateBegin},'yyyy-mm-dd')


  and TO_DATE(#{projectCreateDateEnd},'yyyy-mm-dd') >= project_create_date



     and DATE_FORMAT(project_end_date, '%Y-%m-%d')>=  DATE_FORMAT(#{projectEndDateBegin}, '%Y-%m-%d')   


    and DATE_FORMAT(project_end_date, '%Y-%m-%d') <= DATE_FORMAT(#{projectEndDateEnd}, '%Y-%m-%d')    



     and DATE_FORMAT(project_create_date, '%Y-%m-%d')>=  DATE_FORMAT(#{projectDateBegin}, '%Y-%m-%d')   


    and DATE_FORMAT(project_end_date, '%Y-%m-%d') <= DATE_FORMAT(#{projectDateEnd}, '%Y-%m-%d')
